import React, { useState } from 'react'

export default function TextBox(props) {
    const [text, settext] = useState('');
    const handleClick = () => {
        let newText = text.toUpperCase();
        settext(newText);
    };
    const handleClick2 = () => {
        let newText = text.toLowerCase();
        settext(newText);
    };
    const handleonChange = (event) => {
        settext(event.target.value);
    }
    return (
        <>
            <div className='container my-3'>
                <div className="form-group">
                    <label htmlFor="text">{props.heading}</label>
                    <textarea className="form-control" value={text} onChange={handleonChange} id="myBox" rows="7"></textarea>
                </div>
                <button className="btn btn-primary mx-2" type="submit" onClick={handleClick} >MakeUppercase</button>
                <button className="btn btn-primary" type="submit" onClick={handleClick2} >MakeLowercase</button>
                <div className="container my-3">
                    <h6>Text Summary</h6>
                    <p>{text.split(" ").length} Words and {text.length} Characters</p>
                    <p>{0.008 *text.split(" ").length} min</p>
                    <h6>Preview</h6>
                    <p>{text}</p>
                </div>

            </div>
        </>

    )
}
