import React, { useState } from 'react';
import './App.css';
// import Mydemo from './Mydemo';
import Navbar from './Navbar';
import TextBox from './TextBox';

function App() {
  const [mode,setmode] =useState('dark');
  const [alert,setAlter]=useState(null);
  const toggle =()=>{
    if(mode === 'dark'){
      setmode('light');
      document.body.style.backgroundColor ='#ebedf5';
    }
    else{
      setmode('dark');
      document.body.style.backgroundColor ='#0c2036';
    }
  }
  return (
    <div className='blank'>
    {/* <Navbar/> */}
    <Navbar title="Portfolio" about="About" mode={mode} toggle={toggle}/> 
    {/* <Mydemo/> */}
    <Alter a />
    <TextBox heading="Enter the text to analyze"  mode={mode} />
    </div> 
  );
}

export default App;
